// tests that `cast mktx --create` is working correctly.
casttest!(
    #[ignore = "flaky"]
    test_zk_cast_mktx_contract_deploy,
    async |_prj, cmd| {
        let node = ZkSyncNode::start().await;
        let url = node.url();

        let (_, private_key) = ZkSyncNode::rich_wallets()
            .next()
            .map(|(addr, pk, _)| (addr, pk))
            .expect("No rich wallets available");

        let output = cmd
            .cast_fuse()
            .args([
                "mktx",
                "--rpc-url",
                &url,
                "--private-key",
                private_key,
                "--zksync",
                "--create",
                COUNTER_BYTECODE,
            ])
            .assert_success()
            .get_output()
            .stdout_lossy();

        let output: String = output.trim().parse().unwrap();
        assert!(output.contains(""));

        // empty contract with constructor(uint256) function
        let bytecode_with_constructor = "";

        let output = cmd
            .cast_fuse()
            .args([
                "mktx",
                "--rpc-url",
                &url,
                "--private-key",
                private_key,
                "--zksync",
                "--create",
                bytecode_with_constructor,
                "constructor(uint256)",
                "1",
            ])
            .assert_success()
            .get_output()
            .stdout_lossy();
        let output: String = output.trim().parse().unwrap();
        assert!(output.contains(""))
    }
);

